#import "DBTool.h"
#import <FMDB.h>
#import "ProvinceItem.h"

/** 数据库队列 */
static FMDatabaseQueue *_queue;

@implementation DBTool

+ (void)initialize
{
    NSString *filePath = [[NSSearchPathForDirectoriesInDomains(NSCachesDirectory, NSUserDomainMask, YES) lastObject] stringByAppendingPathComponent:@"Province.sqlite"];
    
    _queue = [FMDatabaseQueue databaseQueueWithPath:filePath];
    
    [_queue inDatabase:^(FMDatabase *db) {
        BOOL result = NO;
        result = [db executeUpdate:@"CREATE TABLE if not exists Province(ProvinceID text Primary Key ,Name text , Abbreviation text)"];
        if (result) {
            result = [db executeUpdate:@" CREATE TABLE if not exists City(CityID text Primary Key ,ProvinceID text ,Name text)"];
            if (!result) {
                NSLog(@"创建表:City 失败");
            } else {
                [db beginTransaction];
                BOOL isRollback = NO;
                //防止重复插入
                NSUserDefaults *defaults = [NSUserDefaults standardUserDefaults];
                BOOL isInserted = [defaults boolForKey:@"isInserted"];
                if (!isInserted) {
                    @try {
                        NSArray *sqlArray = [self getInsertSql];
                        [sqlArray enumerateObjectsUsingBlock:^(NSString *insertSql, NSUInteger idx, BOOL * _Nonnull stop) {
                            if (![insertSql isEqualToString:@""]) {
                                [db executeUpdate:insertSql];
                            }
                        }];
                    } @catch (NSException *exception) {
                        isRollback = YES;
                        [db rollback];
                        [defaults setBool:NO forKey:@"isInserted"];
                    } @finally {
                        if (!isRollback) {
                            [db commit];
                            [defaults setBool:YES forKey:@"isInserted"];
                            [defaults synchronize];
                        }
                    }
                }
                
            }
        }
        else
        {
            NSLog(@"创建表:Province 失败");
        }
    }];
}

